					var data = JSON.parse(xhr.responseText);
					if (data && data.data) {
						var tableBody = document.querySelector('tbody');
						tableBody.innerHTML = '';
						data.data.forEach(function(crypto) {
							var newRow = '<tr>' +
								'<td>' + crypto.name + '</td>' +
								'<td>' + "$" + number_format(crypto.priceUsd, 2) + " USD" + '</td>' +
								'<td>' + "$" + number_format(crypto.marketCapUsd) + " b" + '</td>' +
								'<td>' + "$" + number_format(crypto.supply, 2) + " m" + '</td>' +
								'<td>' + "$" + number_format(crypto.volumeUsd24Hr, 2) + " b" + '</td>' +
								'<td>' + number_format(crypto.changePercent24Hr, 2) + "%" + '</td>' +
								'<td>' +
								'</tr>';
							tableBody.innerHTML += newRow;
						});
					}
				} else {
					console.error('Failed to fetch data. Status: ' + xhr.status);
				}
			};
			xhr.onerror = function() {
				console.error('Network error occurred');
			};
			xhr.send();
		}

		function number_format(number, decimals) {
			return parseFloat(number).toFixed(decimals).replace(/\d(?=(\d{3})+\.)/g, '$&,');
		}

		fetchData();

		setInterval(fetchData, 60000);
	</script>
	<script>
		document.addEventListener("DOMContentLoaded", function() {
			// Sol div'i seç
			var solDiv = document.getElementById("soldiv");

			// Cardları al
			var cardlar = solDiv.getElementsByClassName("card");

			// Cardları ve içerikleri alarak diziye dönüştür
			var cardBilgileri = Array.from(cardlar).map(function(card) {
				return {
					element: card,
					icerik: card.innerHTML
				};
			});

			// Cardları ve içerikleri rastgele sırala
			cardBilgileri.sort(function() {
				return 0.5 - Math.random();
			});

			// Sıralanmış cardları tekrar div içine yerleştir
			for (var i = 0; i < cardBilgileri.length; i++) {
				solDiv.appendChild(cardBilgileri[i].element);
			}
		});
	</script>

</body>

</html>